    'date': '2022-01-25T21:00:35',
    'date_gmt': '2022-01-25T14:00:35',
    'guid': {'rendered': 'https://dispusip.banyuwangikab.go.id/?p=12729'},
    'modified': '2022-01-27T08:33:12',
    'modified_gmt': '2022-01-27T01:33:12',
    'slug': '12729-2',
